INSERT INTO BOARD 
(
BOARDNO
, EMPID
, BOARDTYPENO
, BOARDTITLE
, BOARDCONTENT
, BOARDWRITEDATE
, BOARDHITCOUNT
)
VALUES (
BOARDNO_SEQ.nextval
, #{empId}
, #{boardTypeNo}
, #{boardTitle}
, #{boardContent}
, sysdate
, 0
)

mapper.select:
SELECT count(*) 
  FROM BOARD
 WHERE BOARDTYPENO in (
					  SELECT BOARDTYPENO
					    FROM BOARDTYPE
					   WHERE 1=1
					     AND BOARDTYPENAME = #{boardType}


					     AND INQUIRYBOARDTYPE = #{inquiryBoardType}


				         AND (
			         	     BOARDDELETED is null 
			         	     OR BOARDDELETED = 0
			         	     )
					  )

SELECT rnum , boardNo , boardTitle , boardWriteDate , boardUpdateDate , boardHitcount , boardDeleted , inquiryBoardType , empName , mediaFileName FROM ( SELECT rownum as rnum , boardNo , boardTitle , boardWriteDate , boardUpdateDate , boardHitcount , boardDeleted , inquiryBoardType , empName , mediaFileName FROM ( SELECT b.boardNo , b.boardTitle , b.boardWriteDate , b.boardUpdateDate , b.boardHitcount , b.boardDeleted , bt.inquiryBoardType , e.empName , m.mediaFileName FROM BOARD b INNER JOIN BOARDTYPE bt ON b.BOARDTYPENO = bt.BOARDTYPENO INNER JOIN EMP e ON b.EMPID = e.EMPID LEFT OUTER JOIN MEDIAFILE m ON b.BOARDNO = m.BOARDNO WHERE 1=1 AND BOARDTYPENAME = #{boardType}
AND INQUIRYBOARDTYPE = #{inquiryBoardType}
AND ( BOARDDELETED is null OR BOARDDELETED = 0 ) ORDER BY boardWriteDate DESC ) WHERE rownum <= #{boardPager.endRowNo} ) WHERE rnum >= #{boardPager.startRowNo}

SELECT count(*) 
  FROM BOARDCOMMENT
 WHERE BOARDNO = #{boardNo}
