INSERT INTO BOARD 
(
BOARDNO
, EMPID
, BOARDTYPENO
, BOARDTITLE
, BOARDCONTENT
, BOARDWRITEDATE
, BOARDHITCOUNT
, BOARDDELETED
)
VALUES (
BOARDNO_SEQ.nextval
, #{empId}
, #{boardTypeNo}
, #{boardTitle}
, #{boardContent}
, sysdate
, 0
, 0
)

mapper.select:
SELECT count(*) FROM BOARD b INNER JOIN BOARDTYPE bt ON b.BOARDTYPENO = bt.BOARDTYPENO WHERE 1=1 AND b.BOARDDELETED = 0 AND bt.BOARDTYPENAME = #{boardType}
AND bt.INQUIRYBOARDTYPE = #{inquiryBoardType}
AND bt.TEAMNO = #{teamNo}
AND b.BOARDNO in (
SELECT BOARDNO FROM BOARD WHERE BOARDCONTENT like '%${searchKeyword}%' AND BOARDDELETED = 0
SELECT BOARDNO FROM BOARD WHERE BOARDTITLE like '%${searchKeyword}%' AND BOARDDELETED = 0
SELECT BOARDNO FROM BOARD b INNER JOIN EMP e ON b.EMPID = e.EMPID WHERE e.EMPNAME like '%${searchKeyword}%' AND b.BOARDDELETED = 0
SELECT BOARDNO FROM BOARDCOMMENT WHERE COMMENTCONTENT like '%${searchKeyword}%' AND COMMENTDELETED = 0
SELECT BOARDNO FROM BOARDCOMMENT bc INNER JOIN EMP e ON bc.EMPID = e.EMPID WHERE e.EMPNAME like '%${searchKeyword}%' AND bc.COMMENTDELETED = 0
)

SELECT rnum , boardNo , boardTitle , boardWriteDate , boardUpdateDate , boardHitcount , boardDeleted , inquiryBoardType , empName , mediaFileName FROM ( SELECT rownum as rnum , boardNo , boardTitle , boardWriteDate , boardUpdateDate , boardHitcount , boardDeleted , inquiryBoardType , empName , mediaFileName FROM ( SELECT b.boardNo , b.boardTitle , b.boardWriteDate , b.boardUpdateDate , b.boardHitcount , b.boardDeleted , bt.inquiryBoardType , e.empName , m.mediaFileName FROM BOARD b INNER JOIN BOARDTYPE bt ON b.BOARDTYPENO = bt.BOARDTYPENO INNER JOIN EMP e ON b.EMPID = e.EMPID LEFT OUTER JOIN MEDIAFILE m ON b.BOARDNO = m.BOARDNO WHERE 1=1 AND b.BOARDDELETED = 0 AND BOARDTYPENAME = #{boardType}
AND INQUIRYBOARDTYPE = #{inquiryBoardType}
AND bt.TEAMNO = #{teamNo}
AND b.BOARDNO in (
SELECT BOARDNO FROM BOARD WHERE BOARDCONTENT like '%${searchKeyword}%' AND BOARDDELETED = 0
SELECT BOARDNO FROM BOARD WHERE BOARDTITLE like '%${searchKeyword}%' AND BOARDDELETED = 0
SELECT BOARDNO FROM BOARD b INNER JOIN EMP e ON b.EMPID = e.EMPID WHERE e.EMPNAME like '%${searchKeyword}%' AND b.BOARDDELETED = 0
SELECT BOARDNO FROM BOARDCOMMENT WHERE COMMENTCONTENT like '%${searchKeyword}%' AND COMMENTDELETED = 0
SELECT BOARDNO FROM BOARDCOMMENT bc INNER JOIN EMP e ON bc.EMPID = e.EMPID WHERE e.EMPNAME like '%${searchKeyword}%' AND bc.COMMENTDELETED = 0
)
ORDER BY boardWriteDate DESC ) WHERE rownum <= #{boardPager.endRowNo} ) WHERE rnum >= #{boardPager.startRowNo}

mapper.update:
UPDATE BOARD
   SET BOARDTITLE = #{boardTitle}
   	 , BOARDCONTENT = #{boardContent}
   	 , BOARDUPDATEDATE = sysdate
 WHERE 1=1
   AND BOARDNO = #{boardNo}
   AND (
   	   BOARDDELETED = 0
   	   OR BOARDDELETED is null
   	   )
