mapper.insert:


		select emp_seq.nextval from dual 

	INSERT 
	  INTO emp (
	       EmpId
	     , EmpPW
	     , EmpName
	     , EmpRank
	     , Role
	     , EmpEnabled)
    VALUES (
    	   TO_CHAR(SYSDATE, 'YY') ||0||0|| #{empId}
    	 , #{empPw}
    	 , #{empName}
    	 , #{empRank}
    	 , #{role}
    	 , 1)

mapper.select:
SELECT count(*) 
  FROM empInfo 
 WHERE empTel=#{empTel}

mapper.update:
UPDATE empInfo 
   SET

	      teamNo = null


		  teamNo = #{teamNo}

 WHERE empId = #{empId}

UPDATE empInfo
   SET teamNo 
       = ( 
       SELECT teamNo 
         FROM team 
        WHERE projectNo = #{projectNo} 
          AND teamName ='미배정'
       )
 WHERE teamNo = #{teamNo}

UPDATE emp
   SET empPw=#{empPw}
 WHERE 1=1 
   AND empId=#{empId}

	UPDATE emp
 	   SET empName = #{empName}
 	     , empRank = #{empRank}
 	     , role = #{role}
 	 WHERE empId = #{empId}

	UPDATE empInfo
 	   SET teamNo = #{teamNo}
 	     , empEmail = #{empEmail}
 	     , empTel = #{empTel}
 	 WHERE empId = #{empId}

UPDATE emp
   SET empEnabled = 0
 WHERE empId = #{empId}
