mapper.insert:


		select emp_seq.nextval from dual 

	INSERT 
	  INTO emp (
	       EmpId
	     , EmpPW
	     , EmpName
	     , EmpRank
	     , Role
	     , EmpEnabled)
    VALUES (
    	   TO_CHAR(SYSDATE, 'YY') ||0||0|| #{empId}
    	 , #{empPw}
    	 , #{empName}
    	 , #{empRank}
    	 , #{role}
    	 , 1)

mapper.select:
select *
	from emp e, empinfo ei
	where e.empId = ei.empId and teamNo = #{teamNo}

mapper.update:
UPDATE empInfo 
   SET

	      teamNo = null


		  teamNo = #{teamNo}

 WHERE empId = #{empId}

mapper.select:
select e.*, eif.emptel, eif.empemail from emp e inner join (select  empid ,emptel, empemail from empinfo ) eif
on e.empid = eif.empid 
where role=#{role}

 	SELECT *
 	  FROM emp 
INNER JOIN empInfo 
        ON emp.empId = empinfo.empId 
     WHERE teamNo=#{teamNo} 
       AND role=#{role}

mapper.update:
UPDATE empInfo
   SET teamNo 
       = ( 
       SELECT teamNo 
         FROM team 
        WHERE projectNo = #{projectNo} 
          AND teamName ='미배정'
       )
 WHERE teamNo = #{teamNo}

UPDATE emp
   SET empPw=#{empPw}
 WHERE 1=1 
   AND empId=#{empId}
