mapper.update:
UPDATE CC
   SET messageImportant = CASE
  WHEN messageNo = #{messageNo} 
   AND empId = #{empId} 
   AND messageImportant = '0' THEN '1'
  WHEN messageNo = #{messageNo} 
   AND empId = #{empId} 
   AND messageImportant = '1' THEN '0'
  ELSE messageImportant
   END

UPDATE CC
   SET messageStatus = CASE
  WHEN messageNo = #{messageNo} 
   AND empId = #{empId} 
   AND messageStatus = 1 THEN 2
  WHEN messageNo = #{messageNo} 
   AND empId = #{empId} 
   AND messageStatus = 2 THEN 1
  ELSE messageStatus
   END

mapper.insert:

	SELECT messageno_seq.nextval 
	  FROM DUAL

INSERT INTO MESSAGE 
          ( 
            messageNo
          , empId
          , messageContent
          , messageTitle
          , messageReservedDate
          , messageStatus
          )
  VALUES (
            #{messageNo}
          , #{empId}
          , #{messageContent}
          , #{messageTitle}
          , #{messageReservedDate}
          , #{messageStatus}
         )


        	 SELECT ccNo_seq.nextval 
        	   FROM DUAL

	    INSERT INTO CC 
	              (
	                ccNo
	              , messageNo
	              , empId
	              , messageStatus
	              , messageChecked
	              , messageImportant
	              , ccType
	              , ccCheckedDate
	              )
	       VALUES (
	        	    #{ccNo}
	        	  , #{messageNo}
	        	  , #{empId}
	        	  , #{messageStatus}
	        	  , #{messageChecked}
	        	  , 0
	        	  , #{ccType}
	        	  , #{ccCheckedDate, jdbcType=NULL}
	    		  )

mapper.update:
		UPDATE MEDIAFILE
    	   SET empId = #{empId}
    	     , messageNo = #{messageNo}
    	 WHERE mediaFileNo = #{mediaFileNo}

mapper.select:
		SELECT *
	      FROM CC
	     WHERE messageNo = #{messageNo}
	       AND empId = #{empId}
         AND ( ccType IN (1,3,4))

		SELECT *
	      FROM CC
	     WHERE ccNo = #{ccNo}
	       AND empId = #{empId}
         AND ( ccType IN (1,3,4))
