mapper.insert:

		SELECT messengerNo_seq.nextval
		  FROM DUAL

	INSERT INTO MESSENGER
	          ( 
	            messengerNo
			  ,	mrNo
			  , empId
			  , messengerContent
			  , messengerDate
			  )
	   VALUES (
        	    #{messengerNo}
        	  , #{mrNo}
        	  , #{empId}
        	  , #{messengerContent}
        	  , sysdate
    		  )

mapper.update:
		UPDATE MESSENGERROOM
    	   SET mrLastChat = #{mrLastChat}
    	     , mrDate = sysdate
    	 WHERE mrNo = #{mrNo}
