mapper.insert:


	select project_seq.nextval from dual 

INSERT 
  INTO project (
       projectNo
     , projectName
     , projectStartDate
     , projectEndDate
     , projectStatus
     , projectCompanyName
     , projectContent
     , projectDeleted
     , empId)
VALUES (
       #{projectNo}
     , #{projectName}
     , #{projectStartDate}
     , #{projectEndDate}
     , #{projectStatus}
     , #{projectCompanyName}
     , #{projectContent}
     , #{projectDeleted}
     , 
	     	null)	


	       #{empId})

mapper.update:
UPDATE PROJECT 
   SET projectName = #{projectName}
     , projectStartDate =#{projectStartDate}
     , projectEndDate = #{projectEndDate}

     , empId=#{empId}

     , projectContent=#{projectContent}

     , projectCompanyName=#{projectCompanyName}

 WHERE 1=1
   AND projectNo = #{projectNo}

UPDATE project
   SET projectDeleted = #{projectDeleted}
 WHERE 1=1
   AND projectNo = #{projectNo}
