mapper.insert:
INSERT INTO SCHEDULE
(
SCHEDULENO
, EMPID
, SCHEDULENAME
, SCHEDULECOMMENT
, SCHEDULESTARTDATE
, SCHEDULEENDDATE
, SCHEDULECOLOR
, SCHEDULECHECKED
)
VALUES 
(
SCHEDULENO_SEQ.nextval
, #{empId}
, #{scheduleName}
, #{scheduleComment}
, #{scheduleStartDate}
, #{scheduleEndDate}
, #{scheduleColor}
, 0
)

mapper.update:
UPDATE SCHEDULE
   SET SCHEDULENAME = #{scheduleName}
     , SCHEDULECOMMENT = #{scheduleComment}
     , SCHEDULESTARTDATE = #{scheduleStartDate}
     , SCHEDULEENDDATE = #{scheduleEndDate}
     , SCHEDULECOLOR = #{scheduleColor}
 WHERE 1=1
   AND SCHEDULENO = #{scheduleNo}

mapper.delete:
DELETE FROM SCHEDULE
 WHERE 1=1
   AND SCHEDULENO = #{scheduleNo}
