mapper.select:
SELECT e.empId , p.projectName , t.teamName , e.empName , e.empRank FROM EMP e INNER JOIN EMPINFO ei ON e.EMPID = ei.EMPID INNER JOIN TEAM t ON ei.TEAMNO = t.TEAMNO INNER JOIN PROJECT p ON t.PROJECTNO = p.PROJECTNO WHERE 1=1 AND e.EMPENABLED = 1 AND p.PROJECTDELETED = 0 AND t.TEAMNAME not in ('고객', '미배정')
AND p.PROJECTNO = #{projectNo}
AND t.TEAMNO = #{teamNo}
AND e.EMPID

mapper.insert:
INSERT INTO TASK
(
TASKNO
, EMPID
, TASKNAME
, TASKCOMMENT
, TASKSTARTDATE
, TASKENDDATE
, TASKSTATUS
, TASKCOLOR
, TASKDELETED
)
VALUES 
( TASKNO_SEQ.nextval
, #{empId}
, #{taskName}
, #{taskComment}
, #{taskStartDate}

, null


, #{taskEndDate}

, #{taskStatus}
, 'red'
, 0
)

mapper.update:
UPDATE TASK
   SET TASKNAME = #{taskName}
     , TASKCOMMENT = #{taskComment}
     , TASKSTARTDATE = #{taskStartDate}

     , TASKENDDATE = null


     , TASKENDDATE = #{taskEndDate}

     , TASKSTATUS = #{taskStatus}
 WHERE 1=1
   AND TASKNO = #{taskNo}
   AND TASKDELETED = 0

mapper.select:
SELECT count(*) FROM TASK WHERE 1=1 AND EMPID = #{empId}
AND TASKSTATUS = '진행완료'
AND TASKDELETED = 0
